mapper.insert:
INSERT INTO event (
    title, description, event_date, location, 
    capacity, current_participants, created_at, updated_at
) VALUES (
    #{title}, #{description}, #{eventDate}, #{location}, 
    #{capacity}, #{currentParticipants}, #{createdAt}, #{updatedAt}
)

mapper.update:
